mapper.select:
SELECT
    m.id
    , m.name
    , m.birthDt
    , l.level
    , l.color
    , d.degree
FROM
    member m
    JOIN level l ON m.levelFk = l.id
    JOIN degree d ON m.degreeFk = d.id
WHERE
    1 = 1
    AND m.name LIKE CONCAT('%', #{name}, '%')
    AND m.birthDt LIKE CONCAT('%', #{birthDt}, '%')
    AND m.levelFk = #{levelFk}
    AND m.degreeFk = #{degreeFk}
    AND m.phoneNo LIKE CONCAT('%', #{phoneNo}, '%')
    AND m.winwinYn = #{winwinYn}
    AND m.sex = #{sex}
    AND m.blackCnt >= #{blackCnt}
    AND m.dormancyYn = #{dormancyYn}
    AND m.leaveYn = #{leaveYn}
    AND m.banYn = #{banYn}


        AND m.joinDt >= #{startJoinDt}




        AND m.joinDt <= #{endJoinDt}




        AND m.leaveDt >= #{startLeaveDt}




        AND m.leaveDt <= #{endLeaveDt}




        AND m.banDt >= #{startBanDt}




        AND m.banDt <= #{endBanDt}

SELECT
    a.id
FROM
    admin a
    join member m on a.memberFk = m.id
WHERE
    1 = 1
    AND m.name = #{name}
    AND m.birthDt = #{birthDt}
    AND m.levelFk = #{levelFk}
    AND m.degreeFk = #{degree}

SELECT
    id
FROM
    member
WHERE
    1 = 1
    AND name = #{name}
    AND id = #{id}
    AND birthDt = #{birthDt}
    AND levelFk = #{levelFk}
    AND degreeFk = #{degree}

mapper.insert:
INSERT INTO member (
    id
    , name
    , birthDt
    , levelFk
    , degreeFk
    , winwinYn
    , sex
    , blackCnt
    , dormancyYn
    , leaveYn
    , banYn
    , joinDt
    , createDt
) VALUES (
    #{id}
    , #{name}
    , #{birthDt}
    , #{levelFk}
    , #{degreeFk}
    , #{winwinYn}
    , #{sex}
    , #{blackCnt}
    , #{dormancyYn}
    , #{leaveYn}
    , #{banYn}
    , #{joinDt}
    , #{createDt}
)

mapper.update:
UPDATE
    member

    name = #{name}
    , birthDt = #{birthDt}
    , levelFk = #{levelFk}
    , degreeFk = #{degree}
    , phoneNo = #{phoneNo}
    , winwinYn = #{winwinYn}
    , sex = #{sex}
    , blackCnt = #{blackCnt}
    , dormancyYn = #{dormancyYn}
    , leaveYn = #{leaveYn}
    , banYn = #{banYn}
    , joinDt = #{joinDt}
    , updateDt = #{updateDt}
    , leaveDt = #{leaveDt}
    , banDt = #{banDt}
    , image = #{image}
    , updateReason = #{updateReason}
    , dormancyReason = #{dormancyReason}
    , leaveReason = #{leaveReason}
    , banReason = #{banReason}

WHERE
    1 = 1
    AND id = #{id}

mapper.select:
SELECT
    id
    , name
    , address
    , winwinYn
FROM
    climbingArea
WHERE
    1 = 1
    AND name LIKE CONCAT('%', #{name}, '%')
    AND price = #{price}
    AND winPrice = #{winPrice}
    AND address = #{address}
    AND winwinYn = #{winwinYn}
    AND closeYn = #{closeYn}

mapper.insert:
INSERT INTO climbingArea (
    id
    , name
    , price
    , winPrice
    , address
    , winwinYn
    , createDt
    , updateDt
    , description
) VALUES (
    #{id}
    , #{name}
    , #{price}
    , #{winPrice}
    , #{address}
    , #{winwinYn}
    , #{createDt}
    , #{updateDt}
    , #{description}
)

mapper.update:
UPDATE
    climbingArea

    name = #{name}
    , price = #{price}
    , winPrice = #{winPrice}
    , address = #{address}
    , winwinYn = #{winwinYn}
    , closeYn = #{closeYn}
    , createDt = #{createDt}
    , updateDt = #{updateDt}
    , description = #{description}

WHERE
    1 = 1
    AND id = #{id}
