mapper.insert:
INSERT INTO meeting (
    id
    , name
    , adminFk
    , climbingAreaFk
    , createDt
    , hostDt
    , time
    , criticalMeetingYn
    , meetingStatusFk
) VALUES (
    #{id}
    , #{name}
    , #{adminFk}
    , #{climbingAreaFk}
    , #{createDt}
    , #{hostDt}
    , #{time}
    , #{criticalMeetingYn}
    , #{meetingStatusFk}
)

mapper.select:
SELECT mee.id id , mee.name meetingName , mem1.name hostName , ca.name climbingAreaName , ca.address address , ca.price price , ca.winPrice winPrice , IF(ca.winwinYn = 'Y', 'O', 'X') winwinYn , mee.hostDt hostDt , LEFT(mee.time, 5) time , IF(mee.criticalMeetingYn = 'Y', 'O', 'X') criticalMeetingYn , ms.status status , IFNULL(mem2.name, '-') updater FROM meeting mee JOIN admin a1 ON mee.adminFk = a1.id JOIN member mem1 ON a1.memberFk = mem1.id JOIN meetingStatus ms ON mee.meetingStatusFk = ms.id JOIN climbingArea ca ON mee.climbingAreaFk = ca.id LEFT JOIN admin a2 ON mee.updaterFk = a2.id LEFT JOIN member mem2 ON a2.memberFk = mem2.id WHERE 1 = 1 AND mee.name LIKE CONCAT('%', #{meetingName}, '%') AND mem1.name LIKE CONCAT('%', #{hostName}, '%') AND ca.name LIKE CONCAT('%', #{climbingAreaName}, '%') AND ca.winwinYn = #{winwinYn} AND mee.criticalMeetingYn = #{criticalMeetingYn} AND mee.meetingStatusFk = #{meetingStatusFk}
AND mee.hostDt >= #{startHostDt}
AND mee.hostDt <= #{endHostDt}

mapper.insert:
INSERT INTO attend (
    id
