mapper.update:
UPDATE
    meeting

    name = #{name}
    , adminFk = #{adminFk}
    , climbingAreaFk = #{climbingAreaFk}
    , hostDt = #{hostDt}
    , time = #{time}
    , updateDt = #{updateDt}
    , criticalMeetingYn = #{criticalMeetingYn}
    , meetingStatusFk = #{meetingStatusFk}

WHERE
    1 = 1
    AND id = #{id}
