mapper.select:
SELECT mee.id id , mee.name meetingName , mem.name hostName , ca.name climbingAreaName , ca.address address , ca.price price , ca.winPrice winPrice , ca.winwinYn winwinYn , mee.hostDt hostDt , mee.time time , ms.status status FROM meeting mee JOIN member mem ON mee.memberFk = mem.id JOIN meetingStatus ms ON mee.meetingStatusFk = ms.id JOIN climbingArea ca ON mee.climbingAreaFk = ca.id WHERE 1 = 1 AND mee.name LIKE CONCAT('%', #{meetingName}, '%') AND mem.name LIKE CONCAT('%', #{hostName}, '%') AND ca.name LIKE CONCAT('%', #{climbingAreaName}, '%') AND ca.winwinYn = #{winwinYn}
AND mee.hostDt >= #{startDt}
AND mee.hostDt <= #{endDt}

mapper.insert:
INSERT INTO meeting (
    id
    , name
    , memberFk
    , climbingAreaFk
    , createDt
    , hostDt
    , time
    , criticalMeetingYn
    , meetingStatusFk
) VALUES (
    #{id}
    , #{name}
    , #{memberFk}
    , #{climbingAreaFk}
    , #{createDt}
    , #{hostDt}
    , #{time}
    , #{criticalMeetingYn}
    , #{meetingStatusFk}
)

INSERT INTO attend (
    id
    , meetingFk
    , memberFk
) VALUES (
    #{id}
    , #{meetingFk}
    , #{memberFk}
)
