mapper.select:
SELECT m.id , CONCAT(m.name, ' ', IFNULL(SUBSTRING(m.birthDt, 3, 2), '')) name , CASE WHEN ad.grade = 60 THEN '회원' WHEN ad.grade = 50 THEN '회원' WHEN ad.grade = 40 THEN '매니저' WHEN ad.grade = 30 THEN '운영진' WHEN ad.grade = 20 THEN '부회장' WHEN ad.grade = 10 THEN '회장' ELSE '회원' END AS grade , d.degree , l.level , l.color , COUNT( CASE WHEN YEAR(me.hostDt) = YEAR(CURDATE()) THEN 1 END ) count_this_year , COUNT( CASE WHEN me.hostDt >= DATE_SUB(CURDATE(), INTERVAL 1 YEAR) THEN 1 END ) count_last_1_year , COUNT( CASE WHEN me.hostDt >= DATE_SUB(CURDATE(), INTERVAL 3 MONTH) THEN 1 END ) count_last_3_months , IFNULL(ad.grade, 70) unsignedGrade FROM member m JOIN level l ON m.levelFK = l.id JOIN degree d ON m.degreeFK = d.id LEFT JOIN admin ad ON m.id = ad.memberFK LEFT JOIN attend at ON m.id = at.memberFK LEFT JOIN meeting me ON at.meetingFK = me.id AND me.hostDt >= DATE_SUB(CURDATE(), INTERVAL 2 YEAR) WHERE m.id != 'TCN0' AND me.meetingStatusFK = '10' AND m.name LIKE CONCAT('%', #{name}, '%') AND m.degreeFK = #{degreeFk}
AND m.joinDt >= #{startJoinDt}
AND m.joinDt <= #{endJoinDt}
GROUP BY m.id, m.name, ad.grade, d.degree, l.level, l.color ORDER BY unsignedGrade, d.id;
